import styled,{css} from 'styled-components';

const FormInputLabel =css`
    $sub-color: grey;
    $main-color: black;
    color: $sub-color;
    font-size: 16px;
    font-weight: normal;
    position: absolute;
    pointer-events: none;
    left: 5px;
    top: 10px;
    transition: 300ms ease all;

`;

const ShrinkedLabel = css`
    $sub-color: grey;
    $main-color: black;
    @mixin shrinkLabel {
    top: -14px;
    font-size: 12px;
    color: $main-color;
    }
    @include shrinkLabel();
`;

const addLabel = (props) => {
    return props.value.length? ShrinkedLabel :FormInputLabel;
};

 export const LabelContainer = css`
    ${addLabel}
`;
export const GroupContainer = styled.div`
 position: relative;
 margin: 45px 0;

 input[type='password'] {
     letter-spacing: 0.3em;
   }
`;

export const FormInputStyled = styled.input`
    $sub-color: grey;
    $main-color: black;
    background: none;
    background-color: white;
    color: $sub-color;
    font-size: 18px;
    padding: 10px 10px 10px 5px;
    display: block;
    width: 100%;
    border: none;
    border-radius: 0;
    border-bottom: 1px solid $sub-color;
    margin: 25px 0;

    &:focus {
    outline: none;
    }
    @mixin shrinkLabel {
        top: -14px;
        font-size: 12px;
        color: $main-color;
        }
    &:focus ~ ${FormInputLabel} {
    @include shrinkLabel();
    }
`;
